SELECT r.role_id as id, pid as "pId", name AS "name", ( CASE WHEN (pid = 0 OR pid IS NULL) THEN 'true' ELSE 'false' END ) as "open", ( CASE WHEN (r1.role_id = 0 OR r1.role_id IS NULL) THEN 'false' ELSE 'true' END ) as "checked" FROM sys_role r LEFT JOIN ( SELECT role_id FROM sys_role WHERE role_id IN
#{i}
) r1 ON r.role_id = r1.role_id ORDER BY pid,sort ASC

SELECT r.role_id as id, pid as "pId", name AS "name", ( CASE WHEN (pid = 0 OR pid IS NULL) THEN '1' ELSE '0' END ) as "open", ( CASE WHEN (r1.role_id = 0 OR r1.role_id IS NULL) THEN '0' ELSE '1' END ) as "checked" FROM sys_role r LEFT JOIN ( SELECT role_id FROM sys_role WHERE role_id IN
#{i}
) r1 ON r.role_id = r1.role_id ORDER BY pid,sort ASC
